import axios from 'axios'
import cover from './cover.jpeg'
export default function Books() {
const [books, setBooks]=useState([])
    useEffect(()=>{
        async function getBooks() {
            const res=await axios.get('http://127.0.0.1:2014/books')

            setBooks(res.data)
            console.log(res.data)
        }
        getBooks()
    },[])
    // if (!Array.isArray(Books)) {
    //     return <p>No books found.</p>
    //   }

  return (
    <div className='grid justify-items-center grid-cols-1 sm:gr gap-x-8 gap-y-6 '>
       {books.map(book => (

        <div key={book.id}>
        <img src={cover} alt="./cover.jpeg" className=' bg-sky-200 h-72 w-56'/>

          <ul className='font-bold mb-4'>
            {book.title}
          </ul>
        <ul className=""> <span className='font-semibold'>Author:{"  "}</span>
          {book.author}
        </ul>

        </div>
      ))}


    </div>
  )
}
