import React, { useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { Navigate } from "react-router-dom";

export default function Add() {
  const [title, setTitle] = useState();
  const [author, setAuthor] = useState();
  const [cover, setCover] = useState();

  async function saveBook(e) {
    e.preventDefault();
    try {
      await axios
        .post("http://127.0.0.1:2014/add", { title, author, cover })
        .then((res) => {
          // console.log(res.data);
          toast.success("Book Added Successfully");
          setTitle("");
          setAuthor("");
          setCover("");
        })
    } catch (error) {
      toast.error("Something went wrong");
    }
  }

  return (
    <div className="container mx-auto">
      <form className="sm:pl-96 mx-auto">
        <div className="font-semibold mb-4 px-2">Add New Book</div>

        <div className="">
          <div className="relative z-0 w-96 mb-6 group px-2">
            <input
              type="text"
              name="floating_title"
              value={title}
              className="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none dark:text-white dark:border-gray-600 dark:focus:border-blue-500 focus:outline-none focus:ring-0 focus:border-blue-600 peer"
              onChange={(e) => setTitle(e.target.value)}
              required
            />
            <label
              htmlFor=""
              className="peer-focus:font-medium absolute text-sm text-gray-500 dark:text-gray-400 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:left-0 peer-focus:text-blue-600 peer-focus:dark:text-blue-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6"
            >
              Title
            </label>
          </div>
          <div className="relative z-0 w-96 mb-6 group px-2">
            <input
              type="text"
              name="author"
              value={author}
              className="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none dark:text-white dark:border-gray-600 dark:focus:border-blue-500 focus:outline-none focus:ring-0 focus:border-blue-600 peer"
              onChange={(e) => setAuthor(e.target.value)}
              required
            />
            <label
              htmlFor="author"
              className="peer-focus:font-medium absolute text-sm text-gray-500 dark:text-gray-400 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:left-0 peer-focus:text-blue-600 peer-focus:dark:text-blue-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6"
            >
              Author
            </label>
          </div>
          <div className="relative z-0 w-96 mb-6 group px-2">
            <input
              type="file"
              name="repeat_filename"
              value={cover}
              className="block py-2.5 px-0 w-full text-sm text-gray-900 bg-transparent border-0 border-b-2 border-gray-300 appearance-none dark:text-white dark:border-gray-600 dark:focus:border-blue-500 focus:outline-none focus:ring-0 focus:border-blue-600 peer"
              onChange={(e) => setCover(e.target.value)}
              required
            />
            <label
              htmlFor="file"
              className="peer-focus:font-medium absolute text-sm text-gray-500 dark:text-gray-400 duration-300 transform -translate-y-6 scale-75 top-3 -z-10 origin-[0] peer-focus:left-0 peer-focus:text-blue-600 peer-focus:dark:text-blue-500 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-0 peer-focus:scale-75 peer-focus:-translate-y-6"
            >
              Select Cover Photo
            </label>
            <button
              type="submit"
              onClick={saveBook}
              className=" text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm w-full sm:w-96 px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"
            >
              Submit
            </button>
           NavLink
          </div>
        </div>
      </form>
    </div>
  );
}
