import Books from './Books';
import {BrowserRouter,Routes,Route,Link} from 'react-router-dom';
import Add from './Add';
import Update from './Update';

function App() {
  return (
    <div className=" ">
      <header className="App-header ">
        <p className='text-blue-500 text-4xl font-bold underline py-2 px-4 mb-4'>
          Welcome to the bookshop.
        </p>
        <button className='float-right'>Add Book</button>
      </header>

      <div className=" ">
        <BrowserRouter>
        <Routes>
          <Route path="/" element={<Books/>}/>
          <Route path="/add" element={<Add/>}/>
          <Route path="/edit" element={<Update/>}/>
        </Routes>
        </BrowserRouter>
      </div>
    </div>
  );
}

export default App;
