import axios from 'axios'

export default function Books() {
const [books, setBooks]=useState([])
    useEffect(()=>{
        async function getBooks() {
            const res=await axios.get('http://127.0.0.1:2014/books')

            setBooks(res.data)
            console.log(res.data)
        }
        getBooks()
    },[])
    // if (!Array.isArray(Books)) {
    //     return <p>No books found.</p>
    //   }

  return (
    <div className='flex justify-center items-center'>
       {books.map(book => (

        <div key={book.id}>
        <img src={book.image} alt="cover" className=' bg-sky-200 h-72 w-56'/>

          <ul className=''>
            {book.title}
          </ul>
        <ul className="">
          {book.author}
        </ul>

        </div>
      ))}


    </div>
  )
}
