export const Search = () => {
const [searchTerm, setSearchTerm] = useState('')

const handleSearchInput=(e)=>{
setSearchTerm(e.target.value)
}

const handleSearch = (e)=>{
    e.preventDefault()
    console.log(searchTerm)
}
useEffect(() => {
    handleSearch()
}, []);

  return (
    <div>
        <form>
            <input type="text" placeholder='type to search' name='search' value={searchTerm} onChange={handleSearchInput}/>
            <button></button>
        </form>
    </div>
  )
}
