import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { Link } from'react-router-dom'
import { Toast } from 'react-toastify'
import cover from './cover.jpeg'


export default function Books() {
const [books, setBooks]=useState([])
    useEffect(()=>{
        async function getBooks() {
            const res=await axios.get('http://127.0.0.1:2014/books')

            setBooks(res.data)
            console.log(res.data)
        }
        getBooks()
        Toast("")
    },[])
    // if (!Array.isArray(Books)) {
    //     return <p>No books found.</p>
    //   }

  return (
    <div className='grid justify-items-center grid-cols-1 sm:grid-cols-3 gap-y-6 '>
       {books.map(book => (

        <div key={book.id} className="card rounded">
        <img src={cover} alt="./cover.jpeg" className=' bg-sky-200 h-72 w-56 mx-auto'/>

          <ul className='font-bold mb-4 text-sm '>
            {book.title}
          </ul>
        <ul className="mb-4"> <span className='font-semibold'>Author:{"  "}</span>
          {book.author}
        </ul>
        <div className=' space-x-4 float-right'>
        <button className='bg-blue-700 px-3 rounded text-white font-bold'><Link to="edit"> EDIT</Link></button>
        <button className='bg-red-600 px-3 rounded text-white font-bold'><Link to="">REMOVE</Link></button>

        </div>
        </div>
      ))}



    </div>
  )
}
