import React,{useState, useEffect} from 'react'
import { useNavigate , useParams} from 'react-router-dom'
import axios from 'axios'

export const BookDetails = () => {
    const {id} = useParams()
    const [book, setBook] = useState('')

    useEffect(() => {
        const getBookById = async (id) => {
          try {
            const response = await axios.get(`http://127.0.0.1:2014/books/${id}`);
            setBook(response.data);
            console.log(response.data);
          } catch (error) {
            console.log(error);
          }  
        };
        getBookById(id);
      }, [id]);


    const navigate = useNavigate()
    const redirect = ()=>{
        navigate('/')
    }

  return (
    <>

    <h1 className='text-2xl font-bold mb-4 justify-center items-center flex'>Details:</h1>
    <div className='flex justify-center items-center '>
        <div className='details flex flex-row my-8'>
            <div className=''>
                <p>{book.title}</p> by:
                <p>{book.author}</p>
                <img src={book.cover} alt="" className=' bg-sky-200 h-72 w-56 mx-auto object-cover'/>
            </div>
            <div>
                <h2>Overview</h2>
                <blockquote>
                    Lorem ipsum dolor sit amet consectetur adipisicing elit. Illo minima in animi nemo quibusdam minus, 
                    tempore labore dolor quo quasi ea dicta nisi, corrupti aspernatur odit dolorem eligendi aperiam deleniti?
                </blockquote>
                <button onClick={redirect} className='bg-blue-700 px-3 rounded text-white font-bold mr-4'>Like</button>

                <button onClick={redirect} className='bg-blue-700 px-3 rounded text-white font-bold mr-4'>CLOSE</button>

            </div>
        </div>
    </div>
    </>
  )
}
