import React,{useState, useEffect} from 'react'
import { useNavigate , useParams} from 'react-router-dom'
import axios from 'axios'

export const BookDetails = () => {
    const {id} = useParams()
    const [book, setBook] = useState('')

    useEffect(() => {
        const getBookById = async ({id,book,title,author}) => {
          try {
            const response = await axios.get(`http://127.0.0.1:2014/books/${id}`);
            setBook(response.data);
            console.log(response.data);
          } catch (error) {
            console.log(error);
          }  
        };
        getBookById(id);
      }, [id]);


    const navigate = useNavigate()
    const redirect = ()=>{
        navigate('/')
    }

  return (
    <div>
        <h1>Overview</h1>
        <div>
            {title} by:
            {author}
        </div>
        <button onClick={redirect} className='bg-blue-700 px-3 rounded text-white font-bold mr-4'>CLOSE</button>
    </div>
  )
}
