import React, { useState,useEffect } from 'react'
import axios from 'axios'
import { Link } from'react-router-dom'
import { toast } from 'react-toastify'
//  import cover from './cover.jpeg'
import Update from './Update'

export default function Books() {
const [books, setBooks]=useState([])

// fetch all books from the database
async function getBooks() {
  await axios.get('http://127.0.0.1:2014/books')
  .then(res=>{
    setBooks(res.data)
    // console.log(res.data)
  })

} 
useEffect(()=>{     
  getBooks()  
  },[])

  // handle book deletion
  const removeBook= async (bookId)=>{
      await axios.delete(`http://127.0.0.1:2014/books/${bookId}`)
          .then(res =>{
            // console.log({id:bookId})
            window.location.reload()

            toast.success('item removed');
          })
          .catch(error => toast.error(error))   
    }
//handle editing
const editBook = (bookId) =>{
  window.location.href = `/books/${bookId}`;
  <Link to={<Update/>}></Link>
}

  return (
    <div className='grid justify-items-center grid-cols-1 sm:grid-cols-3 gap-y-6 '>

       {books.map(book => (

        <div key={book.id} className="card rounded">
        <img src={book.cover} alt="" className=' bg-sky-200 h-72 w-56 mx-auto'/>

          <ul className='font-bold mb-4 text-sm '>
            {book.title}
          </ul>
        <ul className="mb-4"> <span className='font-semibold'>Author:{"  "}</span>
          {book.author}
        </ul>
        <div className=' space-x-4 float-right'>
        <button className='bg-blue-700 px-3 rounded text-white font-bold' onClick={()=>editBook(book.id)}> EDIT</button>
        <button className='bg-red-600 px-3 rounded text-white font-bold' onClick={()=>removeBook(book.id)}>REMOVE</button>

        </div>
        </div>
      ))}

    </div>
  )
}
